import React from 'react';
import './Rating.css';

const Rating = ({ value, text, color = '#f8e825' }) => {
    return (
        <div className="rating">
            <span style={{ color }}>
                <i className={
                    value >= 1
                        ? 'fas fa-star'
                        : value >= 0.5
                            ? 'fas fa-star-half-alt'
                            : 'far fa-star'
                }></i>
            </span>
            <span style={{ color }}>
                <i className={
                    value >= 2
                        ? 'fas fa-star'
                        : value >= 1.5
                            ? 'fas fa-star-half-alt'
                            : 'far fa-star'
                }></i>
            </span>
            <span style={{ color }}>
                <i className={
                    value >= 3
                        ? 'fas fa-star'
                        : value >= 2.5
                            ? 'fas fa-star-half-alt'
                            : 'far fa-star'
                }></i>
            </span>
            <span style={{ color }}>
                <i className={
                    value >= 4
                        ? 'fas fa-star'
                        : value >= 3.5
                            ? 'fas fa-star-half-alt'
                            : 'far fa-star'
                }></i>
            </span>
            <span style={{ color }}>
                <i className={
                    value >= 5
                        ? 'fas fa-star'
                        : value >= 4.5
                            ? 'fas fa-star-half-alt'
                            : 'far fa-star'
                }></i>
            </span>
            {text && <span className="rating-text">{text}</span>}
        </div>
    );
};

export default Rating;
