import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { CartContext } from '../context/CartContext';
import './Navbar.css';

const Navbar = () => {
    const { user, logout } = useContext(AuthContext);
    const { cart } = useContext(CartContext);
    const [searchTerm, setSearchTerm] = React.useState('');
    const navigate = import('react-router-dom').useNavigate ? import('react-router-dom').useNavigate() : null; // Dynamic import might not work here, use hook from top level if possible, but React Router v6 usually needs import.
    // Wait, let's fix imports properly.

    return (
        <nav className="navbar">
            <div className="nav-container">
                <div className="nav-logo">
                    <Link to="/">E-Shop</Link>
                </div>

                <div className="nav-search">
                    <input
                        type="text"
                        placeholder="Search products..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        onKeyDown={(e) => {
                            if (e.key === 'Enter') {
                                window.location.href = `/?search=${searchTerm}`;
                            }
                        }}
                    />
                    <button onClick={() => window.location.href = `/?search=${searchTerm}`}>üîç</button>
                </div>

                <ul className="nav-links">
                    <li><Link to="/">Home</Link></li>
                    <li><Link to="/cart">Cart ({cart.length})</Link></li>
                    {user ? (
                        <>
                            <li><Link to="/dashboard">Dashboard</Link></li>
                            {user.role === 'admin' && <li><Link to="/admin">Admin</Link></li>}
                            <li><button onClick={logout}>Logout</button></li>
                        </>
                    ) : (
                        <>
                            <li><Link to="/login">Login</Link></li>
                            <li><Link to="/register">Register</Link></li>
                        </>
                    )}
                </ul>
            </div>
        </nav>
    );
};

export default Navbar;
