import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import { CartContext } from '../context/CartContext';
import './Navbar.css';

const Navbar = () => {
    const { user, logout } = useContext(AuthContext);
    const { cart } = useContext(CartContext);
    const [isMenuOpen, setIsMenuOpen] = React.useState(false);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    return (
        <nav className="navbar">
            <div className="nav-container">
                <div className="nav-logo">
                    <Link to="/">E-Shop</Link>
                </div>

                <button className="menu-toggle" onClick={toggleMenu} aria-label="Toggle navigation">
                    <span className={`hamburger ${isMenuOpen ? 'active' : ''}`}></span>
                </button>

                <ul className={`nav-links ${isMenuOpen ? 'active' : ''}`}>
                    <li><Link to="/" onClick={() => setIsMenuOpen(false)}>Home</Link></li>
                    <li><Link to="/cart" onClick={() => setIsMenuOpen(false)}>Cart ({cart.length})</Link></li>
                    {user ? (
                        <>
                            <li><Link to="/dashboard" onClick={() => setIsMenuOpen(false)}>Dashboard</Link></li>
                            {user.role === 'admin' && <li><Link to="/admin" onClick={() => setIsMenuOpen(false)}>Admin</Link></li>}
                            <li><button onClick={() => { logout(); setIsMenuOpen(false); }}>Logout</button></li>
                        </>
                    ) : (
                        <>
                            <li><Link to="/login" onClick={() => setIsMenuOpen(false)}>Login</Link></li>
                            <li><Link to="/register" onClick={() => setIsMenuOpen(false)}>Register</Link></li>
                        </>
                    )}
                </ul>
            </div>
        </nav>
    );
};

export default Navbar;
