import React, { useState, useEffect } from 'react';
import api from '../api/api';
import './AdminDashboard.css';

const AdminDashboard = () => {
    const [products, setProducts] = useState([]);
    const [orders, setOrders] = useState([]);
    const [activeTab, setActiveTab] = useState('products'); // 'products' or 'orders'
    const [showProductForm, setShowProductForm] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [file, setFile] = useState(null);
    const [uploading, setUploading] = useState(false);
    const [uploadResult, setUploadResult] = useState(null);

    const [formData, setFormData] = useState({
        name: '',
        price: '',
        description: '',
        category: '',
        stock: '',
        imageUrl: ''
    });

    useEffect(() => {
        fetchProducts();
        fetchOrders();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await api.get('/products');
            setProducts(response.data.data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const fetchOrders = async () => {
        try {
            const response = await api.get('/orders');
            setOrders(response.data.data);
        } catch (error) {
            console.error('Error fetching orders:', error);
        }
    };

    const handleInputChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleAddProduct = () => {
        setEditingProduct(null);
        setFormData({
            name: '',
            price: '',
            description: '',
            category: '',
            stock: '',
            imageUrl: ''
        });
        setShowProductForm(true);
    };

    const handleEditProduct = (product) => {
        setEditingProduct(product);
        setFormData({
            name: product.name,
            price: product.price,
            description: product.description,
            category: product.category,
            stock: product.stock,
            imageUrl: product.imageUrl
        });
        setShowProductForm(true);
    };

    const handleSubmitProduct = async (e) => {
        e.preventDefault();

        try {
            if (editingProduct) {
                // Update existing product
                await api.put(`/products/${editingProduct._id}`, formData);
                alert('Product updated successfully!');
            } else {
                // Create new product
                await api.post('/products', formData);
                alert('Product created successfully!');
            }

            setShowProductForm(false);
            fetchProducts();
        } catch (error) {
            alert('Failed to save product: ' + error.response?.data?.message);
        }
    };

    const handleDelete = async (id) => {
        if (!window.confirm('Are you sure you want to delete this product?')) {
            return;
        }

        try {
            await api.delete(`/products/${id}`);
            fetchProducts();
            alert('Product deleted successfully!');
        } catch (error) {
            alert('Failed to delete product');
        }
    };

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleBulkUpload = async (e) => {
        e.preventDefault();
        if (!file) {
            alert('Please select a file');
            return;
        }

        setUploading(true);
        setUploadResult(null);

        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await api.post('/products/bulk-upload', formData, {
                headers: { 'Content-Type': 'multipart/form-data' }
            });

            setUploadResult(response.data.data);
            fetchProducts();
            setFile(null);
            alert('Bulk upload completed!');
        } catch (error) {
            alert('Bulk upload failed: ' + error.response?.data?.message);
        } finally {
            setUploading(false);
        }
    };

    return (
        <div className="admin-container">
            <h1>Admin Dashboard</h1>

            {/* Tab Navigation */}
            <div className="tab-nav">
                <button
                    className={activeTab === 'products' ? 'active' : ''}
                    onClick={() => setActiveTab('products')}
                >
                    Products
                </button>
                <button
                    className={activeTab === 'orders' ? 'active' : ''}
                    onClick={() => setActiveTab('orders')}
                >
                    Orders
                </button>
            </div>

            {/* Products Tab */}
            {activeTab === 'products' && (
                <>
                    {/* Add Product Button */}
                    <div className="action-bar">
                        <button className="btn-add-product" onClick={handleAddProduct}>
                            + Add New Product
                        </button>
                    </div>

                    {/* Product Form Modal */}
                    {showProductForm && (
                        <div className="modal-overlay" onClick={() => setShowProductForm(false)}>
                            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                                <h2>{editingProduct ? 'Edit Product' : 'Add New Product'}</h2>
                                <form onSubmit={handleSubmitProduct}>
                                    <input
                                        type="text"
                                        name="name"
                                        placeholder="Product Name"
                                        value={formData.name}
                                        onChange={handleInputChange}
                                        required
                                    />
                                    <input
                                        type="number"
                                        name="price"
                                        placeholder="Price"
                                        value={formData.price}
                                        onChange={handleInputChange}
                                        required
                                        min="0"
                                        step="0.01"
                                    />
                                    <textarea
                                        name="description"
                                        placeholder="Description"
                                        value={formData.description}
                                        onChange={handleInputChange}
                                        required
                                        rows="3"
                                    />
                                    <input
                                        type="text"
                                        name="category"
                                        placeholder="Category"
                                        value={formData.category}
                                        onChange={handleInputChange}
                                        required
                                    />
                                    <input
                                        type="number"
                                        name="stock"
                                        placeholder="Stock Quantity"
                                        value={formData.stock}
                                        onChange={handleInputChange}
                                        required
                                        min="0"
                                    />
                                    <input
                                        type="url"
                                        name="imageUrl"
                                        placeholder="Image URL"
                                        value={formData.imageUrl}
                                        onChange={handleInputChange}
                                        required
                                    />
                                    <div className="form-actions">
                                        <button type="submit" className="btn-save">
                                            {editingProduct ? 'Update' : 'Create'} Product
                                        </button>
                                        <button
                                            type="button"
                                            className="btn-cancel"
                                            onClick={() => setShowProductForm(false)}
                                        >
                                            Cancel
                                        </button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    )}

                    {/* Bulk Upload Section */}
                    <div className="bulk-upload-section">
                        <h3>Bulk Upload Products (CSV/Excel)</h3>
                        <form onSubmit={handleBulkUpload} className="upload-form">
                            <input
                                type="file"
                                accept=".csv,.xlsx,.xls"
                                onChange={handleFileChange}
                            />
                            <button type="submit" disabled={!file || uploading}>
                                {uploading ? 'Uploading...' : 'Upload File'}
                            </button>
                        </form>

                        {uploadResult && (
                            <div className="upload-result">
                                <p>✅ Uploaded {uploadResult.success.length} products</p>
                                {uploadResult.errors.length > 0 && (
                                    <p>❌ {uploadResult.errors.length} errors</p>
                                )}
                            </div>
                        )}
                    </div>

                    {/* Products Table */}
                    <div className="products-table">
                        <h3>All Products ({products.length})</h3>
                        <table>
                            <thead>
                                <tr>
                                    <th>Name</th>
                                    <th>Price</th>
                                    <th>Category</th>
                                    <th>Stock</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {products.map((product) => (
                                    <tr key={product._id}>
                                        <td>{product.name}</td>
                                        <td>${product.price}</td>
                                        <td>{product.category}</td>
                                        <td>{product.stock}</td>
                                        <td>
                                            <button
                                                className="btn-edit"
                                                onClick={() => handleEditProduct(product)}
                                            >
                                                Edit
                                            </button>
                                            <button
                                                className="btn-delete"
                                                onClick={() => handleDelete(product._id)}
                                            >
                                                Delete
                                            </button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </>
            )}

            {/* Orders Tab */}
            {activeTab === 'orders' && (
                <div className="orders-section">
                    <h3>All Orders ({orders.length})</h3>
                    <div className="orders-list">
                        {orders.map((order) => (
                            <div key={order._id} className="order-card">
                                <div className="order-header">
                                    <h4>Order #{order._id.slice(-8)}</h4>
                                    <span className={`status ${order.paymentStatus}`}>
                                        {order.paymentStatus}
                                    </span>
                                </div>
                                <div className="order-body">
                                    <p><strong>Customer:</strong> {order.user?.name || 'N/A'}</p>
                                    <p><strong>Email:</strong> {order.user?.email || 'N/A'}</p>
                                    <p><strong>Total:</strong> ${order.total}</p>
                                    <p><strong>Date:</strong> {new Date(order.createdAt).toLocaleDateString()}</p>

                                    <div className="order-items">
                                        <strong>Products:</strong>
                                        <ul>
                                            {order.items.map((item, index) => (
                                                <li key={index}>
                                                    {item.product?.name || 'Unknown'} x {item.quantity}
                                                    = ${item.price * item.quantity}
                                                </li>
                                            ))}
                                        </ul>
                                    </div>

                                    <div className="shipping-address">
                                        <strong>Shipping Address:</strong>
                                        <p>
                                            {order.shippingAddress.street}, {order.shippingAddress.city},<br />
                                            {order.shippingAddress.state} {order.shippingAddress.zipCode}, {order.shippingAddress.country}
                                        </p>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminDashboard;
