import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { CartContext } from '../context/CartContext';
import { AuthContext } from '../context/AuthContext';
import api from '../api/api';
import './Checkout.css';

const Checkout = () => {
    const { cart, getTotal, clearCart } = useContext(CartContext);
    const { user } = useContext(AuthContext);
    const navigate = useNavigate();

    const [shippingAddress, setShippingAddress] = useState({
        street: '',
        city: '',
        state: '',
        zipCode: '',
        country: ''
    });

    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    if (!user) {
        navigate('/login');
        return null;
    }

    if (cart.length === 0) {
        navigate('/cart');
        return null;
    }

    const handleChange = (e) => {
        setShippingAddress({
            ...shippingAddress,
            [e.target.name]: e.target.value
        });
    };

    const loadRazorpayScript = () => {
        return new Promise((resolve) => {
            const script = document.createElement('script');
            script.src = 'https://checkout.razorpay.com/v1/checkout.js';
            script.onload = () => resolve(true);
            script.onerror = () => resolve(false);
            document.body.appendChild(script);
        });
    };

    const handleRazorpayPayment = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            // Load Razorpay script
            const scriptLoaded = await loadRazorpayScript();
            if (!scriptLoaded) {
                alert('Razorpay SDK failed to load. Please check your internet connection or disable ad-blockers.');
                setLoading(false);
                return;
            }

            // Create Razorpay order (Calculate price on server)
            const orderResponse = await api.post('/payment/create-order', {
                items: cart.map(item => ({
                    product: item.product._id,
                    quantity: item.quantity
                }))
            });

            const { orderId, currency, key, amount } = orderResponse.data.data;

            // Razorpay options
            const options = {
                key: key,
                amount: total * 100,
                currency: currency,
                name: 'E-Commerce Store',
                description: 'Order Payment',
                order_id: orderId,
                handler: async function (response) {
                    try {
                        // Verify payment
                        await api.post('/payment/verify', {
                            razorpay_order_id: response.razorpay_order_id,
                            razorpay_payment_id: response.razorpay_payment_id,
                            razorpay_signature: response.razorpay_signature
                        });

                        // Complete order
                        await api.post('/payment/complete-order', {
                            razorpay_payment_id: response.razorpay_payment_id,
                            razorpay_order_id: response.razorpay_order_id,
                            items: cart.map(item => ({
                                product: item.product._id,
                                quantity: item.quantity
                            })),
                            shippingAddress
                        });

                        clearCart();
                        alert('Payment successful! Order placed.');
                        navigate('/');
                    } catch (error) {
                        alert('Payment verification failed: ' + error.response?.data?.message);
                        setLoading(false);
                    }
                },
                prefill: {
                    name: user.name,
                    email: user.email
                },
                theme: {
                    color: '#667eea'
                }
            };

            const paymentObject = new window.Razorpay(options);
            paymentObject.open();
            setLoading(false);
        } catch (err) {
            setError(err.response?.data?.message || 'Payment failed');
            setLoading(false);
        }
    };

    return (
        <div className="checkout-container">
            <h2>Checkout</h2>

            {error && <div className="error">{error}</div>}

            <div className="checkout-content">
                <div className="shipping-form">
                    <h3>Shipping Address</h3>
                    <form onSubmit={handleRazorpayPayment}>
                        <input
                            type="text"
                            name="street"
                            placeholder="Street Address"
                            value={shippingAddress.street}
                            onChange={handleChange}
                            required
                        />
                        <input
                            type="text"
                            name="city"
                            placeholder="City"
                            value={shippingAddress.city}
                            onChange={handleChange}
                            required
                        />
                        <input
                            type="text"
                            name="state"
                            placeholder="State"
                            value={shippingAddress.state}
                            onChange={handleChange}
                            required
                        />
                        <input
                            type="text"
                            name="zipCode"
                            placeholder="Zip Code"
                            value={shippingAddress.zipCode}
                            onChange={handleChange}
                            required
                        />
                        <input
                            type="text"
                            name="country"
                            placeholder="Country"
                            value={shippingAddress.country}
                            onChange={handleChange}
                            required
                        />
                        <button type="submit" disabled={loading} className="pay-btn">
                            {loading ? 'Processing...' : `Pay â‚¹${getTotal()} with Razorpay`}
                        </button>
                    </form>
                </div>

                <div className="order-summary">
                    <h3>Order Summary</h3>
                    {cart.map((item) => (
                        <div key={item.product._id} className="summary-item">
                            <span>{item.product.name} x {item.quantity}</span>
                            <span>â‚¹{(item.product.price * item.quantity).toFixed(2)}</span>
                        </div>
                    ))}
                    <div className="summary-total">
                        <span>Total:</span>
                        <span>â‚¹{getTotal().toFixed(2)}</span>
                    </div>
                    <div className="payment-info">
                        <p>ðŸ’³ Secure payment powered by Razorpay</p>
                        <p>ðŸ’° Supports UPI, Cards, Net Banking, Wallets</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Checkout;
